var exec  = require('child_process').exec;
var fs = require('fs');
var cuid = require('cuid');
var colors = require('colors');
var cppModule = require('./cppModule.js');


exports.stats = false;

exports.init = function(option){
	if(option)
	{
		if(option.stats === true )
		{
			console.log('Statistics for CJudge is On'.green);		
			exports.stats = true;			
		}
    }	
	fs.exists( './temp' , function(exists){		
		    if(!exists)
		    {
		        if(exports.stats)
		        {
		        	console.log('INFO: '.cyan + 'temp directory created for storing temporary files.'.cyan )
		        }		    	
		    	fs.mkdirSync('./temp');
		    }
	});
}

exports.compileCPP = function ( envData ,  code , fn ){
	if(exports.stats)
		cppModule.stats = true;
	cppModule.compileCPP(envData , code , fn );
}

exports.compileCPPWithInput = function ( envData , code , input ,  fn ) { 
	if(exports.stats)
		cppModule.stats = true;
	cppModule.compileCPPWithInput(envData , code , input , fn );	
}

exports.compileMultiFileCPPWithInput = function ( envData , code , input ,  fn ) { 
	if(exports.stats)
		cppModule.stats = true;
	cppModule.compileMultiFileCPPWithInput(envData , code , input , fn );	
}
exports.compileMultiFileCPP = function ( envData , code ,  fn ) { 
	if(exports.stats)
		cppModule.stats = true;
	cppModule.compileMultiFileCPP(envData , code , fn );	
}

exports.flushSync = function() {
	    path = '	./temp/';
	    fs.readdir(path, function(err , files){ 
	    	if(!err)
	    	{
	    		for( var i = 0 ; i<files.length ; i++ )
	    		{
	    			
	    			fs.unlinkSync(path+files[i]);	    			

	    		}
	    	}
	    });
}

exports.flush = function(fn) {
	    path = './temp/';
	    fs.readdir(path, function(err , files){ 
	    	if(!err)
	    	{
	    		for( var i = 0 ; i<files.length ; i++ )
	    		{
	    			
	    			fs.unlinkSync(path+files[i]);	    			

	    		}
	    	}
	    });
	    fn();	    
}

exports.fullStat = function(fn){
	var uptime = process.uptime();	
	
	
	var cppCount = 0;

	var total = 0 ;

	var files = fs.readdirSync('temp');
	for(var file in files )
	{
		var stat = fs.statSync('temp/'+files[file]);		
		if(stat.isFile())
		{
			if(files[file].indexOf('.cpp') !== -1)
			{
				cppCount++;
			}
			
		}
		
	}

	var jsonData = { serverUptime : uptime , 
					 fileDetails : {
					 					cpp : cppCount 
					 					
					 			   }
				   }
	if(exports.stats)
	{

		var str  = "Server Statistics".yellow + "\n"
				 + "C-Judge Server Uptime : " + uptime  + "\n" 
				 + "Files on storage :" + "\n"
				 + "C & CPP files : " + cppCount + "\n"
				;
		console.log(str);
	}
	fn(jsonData);

}
